mapper.update:
UPDATE b_student
student_name = #{studentName}, student_class = #{studentClass}, student_role = #{studentRole}, student_group = #{studentGroup}, `password` = #{password}, avatar = #{avatar},
WHERE student_id = #{studentId}

mapper.select:
SELECT * FROM b_student

     AND student_id = #{studentId} 
     AND student_name = #{studentName} 
     AND student_class = #{studentClass} 
     AND student_role = #{studentRole} 
     AND student_group = #{studentGroup}

SELECT * FROM b_student

     AND student_id = #{studentId} 
     AND student_name = #{studentName} 
     AND student_class = #{studentClass} 
     AND student_role = #{studentRole} 
     AND student_group = #{studentGroup}

SELECT student_id FROM b_student

     AND student_id = #{studentId} 
     AND student_name = #{studentName} 
     AND student_class = #{studentClass} 
     AND student_role = #{studentRole} 
     AND student_group = #{studentGroup}
