mapper.update:
UPDATE b_student_task_submit
task_id = #{taskId}, task_handler = #{taskHandler}, task_handler_group_id = #{taskHandlerGroupId}, task_handler_name = #{taskHandlerName}, task_handler_work = #{taskHandlerWork}, submit_link = #{submitLink}, submit_status = #{submitStatus}, submit_time = #{submitTime}, submit_type = #{submitType},
WHERE record_id = #{recordId}

mapper.select:
SELECT * FROM b_student_task_submit

     AND record_id = #{recordId} 
     AND task_id = #{taskId} 
     AND task_handler = #{taskHandler} 
     AND task_handler_group_id = #{taskHandlerGroupId} 
     AND submit_status = #{submitStatus} 
     AND submit_type = #{submitType}

